  "bullet_point_rewrites": [
    {
      "original": "Managed team projects.",
      "revised": "Led a cross-functional team of 5 to deliver 3 high-impact projects, improving operational efficiency by 20%."
    },
    {
      "original": "Improved user engagement.",
      "revised": "Boosted user engagement by 35% through targeted UX redesign and A/B testing."
    }
  ],
  "keyword_mapping": [
    {
      "job_keyword": "Data-Driven Decision Making",
      "resume_match": "Implemented analytics dashboards using SQL and Power BI to support data-informed decisions."
    },
    {
      "job_keyword": "Collaboration",
      "resume_match": "Partnered with cross-functional teams across design and engineering to streamline feature rollouts."
    }
  ],
  "style_suggestions": [
    "Use concise, results-oriented bullet points.",
    "Start each line with strong action verbs like 'Led', 'Built', 'Improved'.",
