parameters.message:
=<!DOCTYPE html>
<html>
  <head>
    <style>
      body {
        font-family: Arial, sans-serif;
        line-height: 1.6;
        color: #333;
        padding: 20px;
      }
      h2 {
        color: #0056b3;
        margin-top: 20px;
        margin-bottom: 10px;
      }
      ul {
        padding-left: 20px;
      }
      li {
        margin-bottom: 8px;
      }
    </style>
  </head>
  <body>
    <h2>Actionable Suggestions to Improve the Resume</h2>
    <ul>
      {{ $json.output.resume_suggestions.map(suggestion => `<li>${suggestion}</li>`).join("\n") }}
    </ul>

    <h2>Keywords/Phrases from Job Description</h2>
    <ul>
      {{ $json.output.job_keywords.map(keyword => `<li>${keyword}</li>`).join("\n") }}
    </ul>
  </body>
</html>

parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "resume_suggestions": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "Actionable edits or improvements tailored to the resume"
    },
    "job_keywords": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "Important keywords or phrases extracted from the job posting"
    }
  },
  "required": ["resume_suggestions", "job_keywords"]
}
